		pq.add(new Edge(S, 0, 0, -1)); // may add more in case of MSSP (Mult-Source)
		while (!pq.isEmpty()) {
			Edge cur = pq.remove();
			if (cur.node.x==T.x &&cur.node.y==T.y) { // remove if all computations are needed
				return cur.cost;

