        HashSet<Integer> leafParents = new HashSet<>();
        HashSet<Integer> leafs = new HashSet<>();
        for (int i = 0; i < n; i++) {
            children[i] = new HashSet<>();
            leafChildren[i] = new LinkedList<>();
            leafs.add(i);
