        }
        return B + ":" + Long.toString(-A);
    }
    private boolean is_first(long A, long B) {
        if (B == 0 && A == Long.MAX_VALUE) {
            return true;
        }
        if (A == 0 && B == Long.MAX_VALUE) {
            return false;
        }
        if (0 < A && 0 < B) {
            return true;
        }
        if (A < 0 && B < 0) {
            return true;
        }
        return false;
    }
    class Group {
        int first = 0;
