			ArrayList<FlowEdge> lst = g.getFlowPath();

			FlowEdge first = lst.get(0);
			FlowEdge last = lst.get(lst.size() - 1);

			int who;

			if (first.to < n + 1) {
				who = first.cap;
			} else {
				who = n - 1 - first.cap;
			}


			int where = last.rev.to - 2 * (n + 1);

//			System.err.println(who + " " + where);

			ans[ptr[where]++] = who;
