                long bb = sc.nextLong();
                b += bb;
                if (!prismall.isEnpty() || prismall.peek() >= a) {
                    // prismall.peek -> pribig, a -> prismall
                    prismall.add(a);
                    small+=a;
                    if (!prismall.isEmpty() && prismall.size() > pribig.size() + 1 ) {
                        long change = prismall.poll;
                        pribig.add(change);
                        small -= change;
                        big += change;
                    }
                }
            }else if(tmp == 2){
                // hanbetsu
                // small = big or small = big + 1
                if (prismall.size() == pribig.size()){
                    ans = prismall.peek();
                    System.out.println(ans + " " + (big-small+b));
                }else{
                    ans = prismall.peek();
                    System.out.println(ans + " " + (big-small+b-prismall.peek()));
                }
            }

        }

        sc.close();
    }
}



