import java.util.Scanner;

public class Main {
	static int n,m,v,p;
	static int a[];
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		 n = sc.nextInt();
		 m = sc.nextInt();
		 v = sc.nextInt();
		 p = sc.nextInt();
		 a = new int[n];
		for(int i=0;i<n;i++) {
			a[i] = sc.nextInt();
