		for(int i = 0;i < N;i++){
			int left = i;
			int right = i;
			while(left - 1 >= 0 && a[left - 1] >= a[i])left--;
			while(right + 1< N && a[right + 1] >= a[i])right++;
			long tmp = (i - left) + (right - i) + (long)(i - left) * (right - i) + 1;
			ans += a[i] * tmp;
