		}

		sum -= (long) (sup - sup / 2) * m;

		System.out.println(sum);
	}

	static int count(int threshold, int n, int[] a) {
		int count = 0;
		for (int i = 0, x; i < n; i++) {
			x = a[i];
			while (x > threshold) {
				x >>= 1;
				count++;
			}
		}
		return count;
	}

