        long first = m * ((long) m * (m - 1)) / 2;
        first %= mod;
        first = (first - (((long)m - 1) * m * (2 * m - 1)) / 6);
        if (first < 0) first += mod;
        first = (first * ((long) n * n)) % mod;
        long second = n * ((long) n * (n - 1)) / 2;
        second %= mod;
        second = (second - (((long)n - 1) * n * (2 * n - 1)) / 6);
        if (second < 0) second += mod;
        second = (second * ((long) m * m)) % mod;
        long res = (choose * ((first + second) % mod)) % mod;
        res %= mod;
        if (res < 0) res += mod;
        out.println(res);
        out.close();
    }

    static long[] fac;
    static long mod = (long) 1e9 + 7;

    static void initFac(long n) {
        fac = new long[(int)n + 1];
        fac[0] = 1;
        for (int i = 1; i <= n; i++) {
            fac[i] = (fac[i - 1]  * i) % mod;
        }
    }

    static long nck(int n, int k) {
        if (n < k)
            return 0;
        long den = inv((int) (fac[k] * fac[n - k] % mod));
        return fac[n] * den % mod;
    }

    static long pow(long b, long e) {
        long ans = 1;
        while (e > 0) {
            if (e % 2 == 1)
                ans = ans * b % mod;
            e >>= 1;
            b = b * b % mod;
        }
        return ans;
    }

    static long inv(long x) {
        return pow(x, mod - 2);
    }


